
let restaurants,neighborhoods,cuisines
var map
var markers=[]
document.addEventListener('DOMContentLoaded',(event)=>{fetchNeighborhoods();fetchCuisines();serviceWorkerRegister();updateRestaurants();createStaticMapHTML();});createStaticMapHTML=()=>{const staticMapContainer=document.getElementById('static-map-container');const mapContainer=document.getElementById('map-container');mapContainer.style.display='none';const button=document.getElementById('btn-map');button.addEventListener('click',()=>{window.initMap();staticMapContainer.style.display='none';mapContainer.style.display='block';});};fetchNeighborhoods=()=>{DBHelper.fetchNeighborhoods((error,neighborhoods)=>{if(error){console.error(error);}else{self.neighborhoods=neighborhoods;fillNeighborhoodsHTML();}});}
fillNeighborhoodsHTML=(neighborhoods=self.neighborhoods)=>{const select=document.getElementById('neighborhoods-select');neighborhoods.forEach(neighborhood=>{const option=document.createElement('option');option.innerHTML=neighborhood;option.value=neighborhood;select.append(option);});}
fetchCuisines=()=>{DBHelper.fetchCuisines((error,cuisines)=>{if(error){console.error(error);}else{self.cuisines=cuisines;fillCuisinesHTML();}});}
fillCuisinesHTML=(cuisines=self.cuisines)=>{const select=document.getElementById('cuisines-select');cuisines.forEach(cuisine=>{const option=document.createElement('option');option.innerHTML=cuisine;option.value=cuisine;select.append(option);});}
window.initMap=()=>{let loc={lat:40.722216,lng:-73.987501};self.map=new google.maps.Map(document.getElementById('map'),{zoom:12,center:loc,scrollwheel:false});updateRestaurants();}
updateRestaurants=()=>{const cSelect=document.getElementById('cuisines-select');const nSelect=document.getElementById('neighborhoods-select');const cIndex=cSelect.selectedIndex;const nIndex=nSelect.selectedIndex;const cuisine=cSelect[cIndex].value;const neighborhood=nSelect[nIndex].value;DBHelper.fetchRestaurantByCuisineAndNeighborhood(cuisine,neighborhood,(error,restaurants)=>{if(error){console.error(error);}else{resetRestaurants(restaurants);fillRestaurantsHTML();}})}
resetRestaurants=(restaurants)=>{self.restaurants=[];const ul=document.getElementById('restaurants-list');ul.innerHTML='';self.markers.forEach(m=>m.setMap(null));self.markers=[];self.restaurants=restaurants;}
fillRestaurantsHTML=(restaurants=self.restaurants)=>{const ul=document.getElementById('restaurants-list');restaurants.forEach(restaurant=>{ul.append(createRestaurantHTML(restaurant));});addMarkersToMap();}
createRestaurantHTML=(restaurant)=>{const li=document.createElement('li');const picture=document.createElement('picture');const image=document.createElement('img');image.className='restaurant-img lazyload';image.src='data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAICTAEAOw==';image.setAttribute('data-src',DBHelper.imageUrlForRestaurant(restaurant)+'.jpg');image.alt=`${restaurant.name} cover photo`;const webp=document.createElement('source');webp.setAttribute('data-srcset',DBHelper.imageUrlForRestaurant(restaurant)+'.webp');webp.setAttribute('type','image/webp');const jpg=document.createElement('source');jpg.setAttribute('data-srcset',DBHelper.imageUrlForRestaurant(restaurant)+'.jpg');jpg.setAttribute('type','image/jpg');picture.append(webp);picture.append(jpg);picture.append(image);li.append(picture);const name=document.createElement('h2');name.innerHTML=restaurant.name;li.append(name);const neighborhood=document.createElement('p');neighborhood.innerHTML=restaurant.neighborhood;li.append(neighborhood);const address=document.createElement('p');address.innerHTML=restaurant.address;li.append(address);const favoriteContainer=document.createElement('div');favoriteContainer.classList.add('favorite-container');const favorite=document.createElement('span');favorite.setAttribute('role','img');favorite.setAttribute('aria-label','heart emoji');favorite.classList.add('normal');favorite.innerText='\u2764';if(restaurant.is_favorite==null||restaurant.is_favorite==undefined){restaurant['is favorite']=false;}
if(restaurant.is_favorite){favorite.classList.add('is-favorite');}
favorite.addEventListener('click',e=>{e.target.classList.toggle('is-favorite');restaurant.is_favorite=!restaurant.is_favorite;DBHelper.favoriteRestaurant(restaurant);});favoriteContainer.append(favorite);li.append(favoriteContainer);const more=document.createElement('a');more.innerHTML='View Details';more.href=DBHelper.urlForRestaurant(restaurant);li.append(more)
return li}
addMarkersToMap=(restaurants=self.restaurants)=>{restaurants.forEach(restaurant=>{const marker=DBHelper.mapMarkerForRestaurant(restaurant,self.map);google.maps.event.addListener(marker,'click',()=>{window.location.href=marker.url});self.markers.push(marker);});}